import ItemListContainer from "./components/ItemListContainer";
import { useState } from "react"

const Main = () => {
  const [estado, setEstado] = useState(5)
  const handleClick = () =>{
    if(estado > 0){
      setEstado(estado - 1)
    }
  }
  return (
    <main>
        <h2>Bienvenidx</h2>
        <h3>Deseamos que nuestro sitio sea de su agrado y tenga una linda experiencia</h3>
        <ItemListContainer greeting={["cliente", "usuario", "vendedor"]}/>
        <h1>CONTADOR: {estado}</h1>
        <button onClick={handleClick}>-</button> <button>+</button>
        <button>FINALIZAR</button>
    </main>
  );
}

export default Main
