import { useState } from "react"


const ItemCount = () => {
    const [estado, setEstado] = useState(0)
    const handleRestar = () =>{
      if(estado > 0){
        setEstado(estado - 1)
      }
    }
    const handleSumar = () => {
      if(estado < 10){
        setEstado(estado + 1)
      }
    }

  return (
    <>
        <h1>CONTADOR: {estado}</h1>
        <button onClick={handleRestar}>-</button> <button onClick={handleSumar}>+</button>
        <button>FINALIZAR</button>
    </>
  )
}

export default ItemCount
